    const theme = {
        backgroundColor: dark ? "#333" : "#fff",
        color: dark ? "#fff" : "#333"
    }

    return (
        <div style={theme}>
            < input type="number" value={number} onChange={e => setNumber(parseInt(e.target.value))} />
